INSERT INTO `foods` (`id`, `id_type`, `id_country`, `id_url`, `name`, `summary`, `detail`, `price`, `promotion_price`, `promotion`, `image`, `update_at`, `unit`, `soluotxem`) VALUES
(1, 2, 1, 1, 'Bánh canh', '<ol>\r\n	<li>\r\n	<p>1&nbsp;c&ograve;n ghẹ xanh Phan Thiết 350gr</p>\r\n	</li>\r\n	<li>\r\n	<p>500 gr&nbsp;xương heo</p>\r\n	</li>\r\n	<li>\r\n	<p>1 Bịch&nbsp;b&aacute;nh canh bột lọc nhỏ</p>\r\n	</li>\r\n	<li>\r\n	<p>100 gr&nbsp;nấm rơm</p>\r\n	</li>\r\n	<li>\r\n	<p>H&agrave;nh l&aacute;, ng&ograve; r&iacute;, 2 củ h&agrave;nh t&iacute;m lột vỏ</p>\r\n	</li>\r\n	<li>\r\n	<p>Dầu điều, dầu ăn, muối ớt xanh, ớt tươi, ti&ecirc;u, x&agrave; l&aacute;ch</p>\r\n	</li>\r\n	<li>\r\n	<p>Hạt n&ecirc;m, muối, nước mắm, đường ph&egrave;n</p>\r\n	</li>\r\n</ol>', '<p><strong>B&aacute;nh canh Trảng B&agrave;ng (T&acirc;y Ninh)</strong></p>\r\n\r\n<p>L&agrave; một trong những m&oacute;n ăn nổi tiếng của T&acirc;y Ninh, m&oacute;n b&aacute;nh canh Trảng B&agrave;ng được chế biến hết sức c&ocirc;ng phu, sợi b&aacute;nh canh được l&agrave;m bằng loại gạo qu&yacute; nhất như gạo N&agrave;ng Thơm hay Gạo Đ&agrave;o. Gạo được ng&acirc;m qua đ&ecirc;m sau đ&oacute; được mang xay nhuyễn v&agrave; mang đi hấp ch&iacute;n th&agrave;nh những sợi b&aacute;nh trắng tinh.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"https://4.bp.blogspot.com/-N9Q9AswSpBQ/Vjh7HXdq12I/AAAAAAAAA8w/gkb9_LGdAgs/s320/banh-canh-viet-nam-cuc-ngon.jpg\" style=\"height:259px; width:320px\" /></p>\r\n\r\n<p><br />\r\nB&aacute;nh canh Trảng B&agrave;ng l&agrave; m&oacute;n ăn nổi tiếng của tỉnh T&acirc;y Ninh.<br />\r\n<br />\r\nT&ocirc; b&aacute;nh canh Trảng B&agrave;ng c&oacute; vị cay của ớt đỏ, ti&ecirc;u đen, m&ugrave;i thơm của h&agrave;nh xanh. Khi ăn v&agrave;o sẽ c&oacute; vị b&eacute;o ngọt của thịt, độ dai nhưng mềm của sợi b&aacute;nh canh cộng th&ecirc;m vị chua cay mặn của nước mắm. Đặc biệt l&agrave; những l&aacute;t thịt heo mỏng được cắt kh&eacute;o l&eacute;o c&oacute; cả da, g&acirc;n thịt, gi&ograve; heo, c&ugrave;ng nước l&egrave;o thơm ngọt, trong vắt đ&atilde; l&agrave;m n&ecirc;n sức hấp dẫn kh&oacute; c&oacute; thể cưỡng lại của b&aacute;nh canh Trảng B&agrave;ng.<br />\r\n<br />\r\n<strong>B&aacute;nh canh Nam Phổ</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/2-6.jpg\" /></p>\r\n\r\n<p><br />\r\nB&aacute;nh canh Nam Phổ c&oacute; nước l&egrave;m sền sền, c&oacute; m&agrave;u đỏ của gạch cua.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; m&oacute;n b&aacute;nh canh nổi tiếng của l&agrave;ng Nam Phổ, TP. Huế. Điều đặt biệt của b&aacute;nh canh Nam Phổ ch&iacute;nh l&agrave; nước l&egrave;o của b&aacute;nh canh phải c&oacute; m&agrave;u đỏ cam của gạch cua v&agrave; t&ocirc;m, đồng thời nước l&egrave;o phải sền sệt kh&ocirc;ng qu&aacute; lỏng cũng kh&ocirc;ng qu&aacute; đặc.<br />\r\n<br />\r\nSợi b&aacute;nh canh Nam Phổ cũng được l&agrave;m bằng bột gạo, sợi b&aacute;nh canh tr&ograve;n v&agrave; ngắn chứ kh&ocirc;ng vu&ocirc;ng v&agrave; d&agrave;i như b&aacute;nh canh bột m&igrave; hay b&aacute;nh canh bột lọc của miền Trung. B&aacute;nh canh được ăn c&ugrave;ng thịt heo v&ograve; vi&ecirc;n nhỏ, gạch cua được x&agrave;o ch&iacute;n v&agrave; ăn c&ugrave;ng nước l&egrave;o thơm ngọt.<br />\r\n<br />\r\nB&aacute;nh canh bột xắt miền T&acirc;y<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/3-7.jpg\" /></p>\r\n\r\n<p><br />\r\nB&aacute;nh canh được l&agrave;m từ gạo ng&acirc;m qua đem sau đ&oacute; được xay nhuyễn th&agrave;nh bột nước rồi cho v&agrave;o t&uacute;i vải đăng cho thật r&aacute;o nước. Kế đến người ta cho bột ra m&acirc;m v&agrave; nhồi bột thật đều tay sau cho kh&ocirc;ng qu&aacute; kh&ocirc; cũng kh&ocirc;ng qu&aacute; nh&atilde;o.<br />\r\n<br />\r\nB&aacute;nh canh bột xắt kh&ocirc;ng được nấu c&ugrave;ng gi&ograve; heo, thịt heo hay cua, t&ocirc;m m&agrave; lại được nấu c&ugrave;ng thịt vịt v&agrave; huyết vịt. M&oacute;n b&aacute;nh canh bột sắt được ăn c&ugrave;ng nước mắm gừng tạo n&ecirc;n m&ugrave;i vị hấp dẫn, nức l&ograve;ng kh&aacute;ch gần xa.<br />\r\n<br />\r\n<strong>B&aacute;nh canh cua Huế</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/4-5.jpg\" /></p>\r\n\r\n<p><br />\r\nB&aacute;nh canh cua được l&agrave;m rất đơn giản nhưng khi ăn lại rất hấp dẫn<br />\r\n<br />\r\nĐ&acirc;y được xem l&agrave; m&oacute;n ăn b&igrave;nh dị của xứ miền Trung, với c&aacute;ch l&agrave;m rất đơn giản nhưng khi ăn v&agrave;o lại v&ocirc; c&ugrave;ng hấp dẫn. Để l&agrave;m bột b&aacute;nh canh người ta cho bột v&agrave; muối v&agrave;o nước n&oacute;ng rồi nhồi cho đến khi bột c&oacute; độ dẻo sau đ&oacute; bột được c&aacute;n dẹp v&agrave; cắt từng miếng mỏng. Kế đến người ta đun nước s&ocirc;i v&agrave; cho bột b&aacute;nh canh v&agrave;o luộc.<br />\r\n<br />\r\nĐể c&oacute; m&oacute;n nước d&ugrave;ng người ta hầm d&ugrave;ng xương ống heo đun thật ch&iacute;n, sau đ&oacute; cho thịt cua đ&atilde; x&agrave;o qua với tỏi v&agrave;o trong nước d&ugrave;ng, cuối c&ugrave;ng l&agrave; thịt v&ograve; vi&ecirc;n v&agrave; thịt nạc.<br />\r\n<br />\r\n<strong>B&aacute;nh canh chả c&aacute; Nha Trang</strong><br />\r\n<br />\r\nThưởng thức b&aacute;nh canh chả c&aacute; nhất định kh&ocirc;ng thể thiếu nước mắm Phan Rang, được tinh chế ho&agrave;n to&agrave;n từ c&aacute; cơm cộng th&ecirc;m những sơi b&uacute;n thanh mảnh, c&ugrave;ng chả c&aacute; chi&ecirc;n v&agrave;ng gi&ograve;n. Khi ăn v&agrave;o ta sẽ cảm nhận m&ugrave;i vị nước l&egrave;o được nấu từ hải sản biển tạo n&ecirc;n sức hấp dẫn k&igrave; lạ của b&aacute;nh canh chả c&aacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/6-4.jpg\" /></p>\r\n\r\n<p><br />\r\nB&aacute;nh canh chả c&aacute; kh&ocirc;ng thể thiếu nước mắm Phan Rang.<br />\r\n<br />\r\nĐiểm nhấn của b&aacute;nh canh ch&iacute;nh l&agrave; chả c&aacute;, để c&oacute; chả c&aacute; thơm ngon th&igrave; phải d&ugrave;ng loại c&aacute; tươi ngon nhất: c&aacute; mối, c&aacute; thu, c&aacute; cờ, c&aacute;c chuồn&hellip; Chả c&aacute; c&oacute; hai loại l&agrave; chả hấp v&agrave; chả chi&ecirc;n. Chả chi&ecirc;n c&oacute; vị thơm gi&ograve;n, chả hấp th&igrave; c&oacute; độ dẻo ngọt tuỳ theo sở th&iacute;ch v&agrave; khẩu vị của mỗi người.<br />\r\n<br />\r\n<strong>B&aacute;nh canh c&aacute; l&oacute;c</strong><br />\r\nỞ Huế b&aacute;nh canh c&aacute; l&oacute;c kh&ocirc;ng chỉ l&agrave; m&oacute;n ăn d&acirc;n d&atilde; tr&ecirc;n c&aacute;c đường phố m&agrave; c&ograve;n trở th&agrave;nh một đặc sản nổi tiếng của đất cố đ&ocirc;, s&ocirc;ng Hương n&uacute;i Ngự.<br />\r\n<br />\r\nM&oacute;n c&aacute; l&oacute;c tuy c&oacute; th&agrave;nh phần kh&aacute; đơn giản, chỉ bao gồm sợi b&aacute;nh canh được l&agrave;m từ bột gạo v&agrave; thịt c&aacute; l&oacute;c đồng nhưng c&aacute;ch thức chế biến lại đ&ograve;i hỏi rất nhiều về sự tỉ mỉ v&agrave; c&ocirc;ng phu của người l&agrave;m.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/7-5.jpg\" /></p>\r\n\r\n<p><br />\r\n<strong>Được mệnh danh l&agrave; đặc sản của đất cố đ&ocirc;.</strong><br />\r\n<br />\r\nĐể l&agrave;m sợi b&aacute;nh canh th&igrave; bột gạo phải c&oacute; được độ dẻo dai v&agrave; vị ngọt tự nhi&ecirc;n. Kế tiếp việc chọn c&aacute; l&oacute;c cũng đ&ograve;i hỏi sự đầu tư kh&ocirc;ng k&eacute;m, c&aacute; l&oacute;c phải l&agrave; c&aacute; đ&aacute;nh bắt ở đồng, cỡ lớn, thịt săn chắc. C&aacute; khi được hấp vừa ch&iacute;n tới th&igrave; lọc kĩ từng phần thịt nạc ra khỏi xương. Đầu v&agrave; đu&ocirc;i c&aacute; đem gi&atilde; th&agrave;nh miếng nhỏ bọc vải s&aacute;ch v&agrave; đem ninh c&ugrave;ng gia vị, việc n&agrave;y gi&uacute;p nước l&egrave;o trở n&ecirc;n thanh ngọt hơn.&nbsp;<br />\r\n<br />\r\n<strong>B&aacute;nh Canh Ghẹ Vũng T&agrave;u</strong><br />\r\n<br />\r\nL&agrave; m&oacute;n b&aacute;nh canh được biến tấu từ b&aacute;nh canh cua, nhưng để chế biến b&aacute;nh canh ghẹ th&igrave; v&ocirc; c&ugrave;ng cầu k&igrave;, đ&ograve;i hỏi rất nhiều sự tỉ mỉ, đặc biệt phải chọn được ghẹ tươi, thịt nhiều v&agrave; chắc.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://img2.news.zing.vn/2012/02/03/8-5.jpg\" /></p>\r\n\r\n<p><br />\r\nĐể c&oacute; m&oacute;n b&aacute;nh canh ngon phải chọn ghẹ tươi, thật chắc thịt.<br />\r\n<br />\r\nMột t&ocirc; b&aacute;nh canh ghẹ phải c&oacute; ghẹ được luộc nguy&ecirc;n con, chả t&ocirc;m, tiết heo, nấm v&agrave; sợi b&aacute;nh canh bột lọc. Nước l&egrave;o được nấu rất s&aacute;nh, c&oacute; vị đậm đ&agrave; của ghẹ v&agrave; ngọt thanh của nước ghẹ. Thịt ghẹ khi ăn v&agrave;o c&oacute; vị thơm của h&agrave;nh tỏi, đậm đ&agrave; của gia vị đ&atilde; ướp rất c&ocirc;ng phu. Để c&oacute; nồi nước l&egrave;o thơm trong vắt, ta phải chọn ghẹ tươi mới cho ra được m&ugrave;i vị đặc trưng, vốn c&oacute; của b&aacute;nh canh ghẹ Vũng T&agrave;u.</p>', 25000, 0, 'khăn lạnh', '15149217521.jpg', '2018-01-02', 'dĩa', 4241),
(2, 10, 1, 2, 'Nước giải khác các loại', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...', 'Chèn thập cẩm, chè đâu xanh, trà sữa trân châu,... được chế biến từ chính các đầu bếp của nhà hàng chúng tôi.', 45000, 0, 'nước ngọt, khăn lạnh', 'nuoc_giai_khat_cac_loai.jpg', '2013-12-29', 'đĩa', 0),
(3, 2, 1, 3, 'Súp Gà Ngô non', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...', 'Cách làm:\r\n\r\n\r\nĐun nước dùng sôi, cho thịt gà vào, nêm muối tiêu, đun sôi nhỏ lửa 10 phút. Nếu bạn sợ gà quá nhừ thì có thể vớt thịt gà ra.', 25000, 0, 'nước ngọt, khăn lạnh', 'sup_ngo_non.jpg', '2013-12-29', 'đĩa', 0),
(4, 1, 1, 4, 'Salát cà chua dưa chuột', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...Cách làm: dưa chuột cà chua, dứa, gọt vỏ, rửa sạch, thái lát mỏng. Bày xen kẽ các nguyên liệu trên theo hình tròn hoặc hình dẻ quạt.Tỏi, ớt đập dập, băm nhỏ, cho vào tô với một chút dấm đường và một chút muối. Quậy đều hỗn hợp trên, nếm thử thấy có vị chua, ngọt vừa ăn là được. Dội hỗn hợp trên vào đĩa nguyên liệu đã được trình bày.', 25000, 0, 'nước ngọt, khăn lạnh', 'salat_ca_chua_dua_chuot.jpg', '2013-12-29', 'đĩa', 0),
(5, 1, 1, 5, 'Nộm gà xé phay', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...', 'Nguyên liệu:\r\n250g thịt ức gà\r\n½ củ hành tây to (hoặc 1 củ nhỏ)\r\n½ củ cà rốt\r\n1 chút lạc rang\r\nRau mùi, húng\r\nNước mắm, chanh, đường', 80000, 0, 'nước ngọt, khăn lạnh', 'nom_ga_xe_phay.jpg', '2013-12-29', 'đĩa', 0),
(6, 2, 1, 6, 'Nem Phù đổng', 'Nguyên liệu: 3 quả dưa chuột, 3 quả cà chua, 1/2 quả dứa, xà lách xoăn, hành tây, tỏi, đường, dấm, ớt...', 'Gọi một đĩa nem ốc mà phải đợi đến 15 phút. Nem được gói từ trước, đến khi khách gọi nhà hàng mới mang vào rán. Trong lúc đợi có thể gọi bún ốc, Gọi một đĩa nem ốc mà phải đợi đến 15 phút. Nem được gói từ trước, đến khi khách gọi nhà hàng mới mang vào rán. Trong lúc đợi có thể gọi bún ốc, ', 80000, 0, 'nước ngọt, khăn lạnh', 'nem-oc-phu-dong.jpg', '2013-12-29', 'đĩa', 0),
